script:
frappe.ui.form.on("Sales Invoice Item", "item_code", function(frm, cdt, cdn) {
	var d = locals[cdt][cdn];
	d["warehouse"] = cur_frm.doc.spn_warehouse;
});

frappe.ui.form.on("Sales Invoice", "spn_warehouse", function(frm) {
    if(frm.doc.spn_warehouse && frm.doc.territory && frm.doc.customer_group) {
        items = frm.doc.items;
        for (var i=0; i <= items.length - 1; i++) {
            items[i].warehouse = cur_frm.doc.spn_warehouse;
        }

        //Fetch and set letterhead
        frappe.call({
            method: "skynpronaturals_erpnext.api.get_spn_letter_head",
            args: {spn_warehouse: frm.doc.spn_warehouse }, 
            callback: function(r) {
                if (!r.exc) {
                    cur_frm.set_value("letter_head", r.message);
                } else {
                    frappe.msgprint("Check letterhead field in selected Warehouse.")
                }
            }
        });

            frappe.call({
                method: "skynpronaturals_erpnext.api.get_naming_series",
                args: { 
                 "spn_warehouse": frm.doc.spn_warehouse,
                 "cust_ter": frm.doc.territory,
                 "cust_group": frm.doc.customer_group
                },
                callback: function(r){
                    //frappe.msgprint(r);
                    cur_frm.set_value("naming_series", r.message);
                }
            });

        //Fetch terms by territory and set.
        frappe.call({
            method: "skynpronaturals_erpnext.api.get_terms_by_warehouse_state",
            args: { 
             "spn_warehouse": frm.doc.spn_warehouse
            },
            callback: function(r){

                cur_frm.set_value("tc_name", r.message);
            }
        });
    }
});

frappe.ui.form.on("Purchase Receipt", "spn_warehouse", function(frm) {
    if(frm.doc.spn_warehouse) {
        items = frm.doc.items;
        for (var i=0; i <= items.length - 1; i++) {
            items[i].warehouse = cur_frm.doc.spn_warehouse;
        }

        //Fetch and set letterhead
        frappe.call({
            method: "skynpronaturals_erpnext.api.get_spn_letter_head",
            args: {spn_warehouse: frm.doc.spn_warehouse }, 
            callback: function(r) {
                if (!r.exc) {
                    cur_frm.set_value("letter_head", r.message);
                } else {
                    frappe.msgprint("Check letterhead field in selected Warehouse.")
                }
            }
        });
       }
});

frappe.ui.form.on("Stock Entry", "onload", function(frm) {
	frm.set_df_property("spn_to_warehouse", "hidden", 1);
});

frappe.ui.form.on("Stock Entry", "refresh", function(frm) {
	frm.add_custom_button(__("Fetch Items from Transit Entry"), function() {
		frappe.prompt([
			{'fieldname': 'stock_entry', 'fieldtype': 'Link', 'options':'Stock Entry','label':'Stock Entry' }
		],
		function(value){
			console.log(value);
			fetch_and_set_items(frm, value["stock_entry"]);
		},
		'Select Transit Entry',
		'Select'
		)
	});
});

frappe.ui.form.on("Stock Entry", "spn_to_warehouse", function(frm) {
	if(frm.doc.spn_to_warehouse) {
		items = frm.doc.items;
		for (var i=0; i <= items.length - 1; i++) {
			items[i].spn_t_warehouse = cur_frm.doc.spn_to_warehouse;
		}
	}
});

frappe.ui.form.on("Stock Entry", "from_warehouse", function(frm) {
	if(frm.doc.from_warehouse) {
		frappe.call({
			method: "skynpronaturals_erpnext.api.get_spn_letter_head",
			args: {spn_warehouse: cur_frm.doc.from_warehouse }, 
			callback: function(r) {
				if (!r.exc) {
					cur_frm.set_value("letter_head", r.message);
				} else {
					frappe.msgprint("Check letterhead field in selected Warehouse.")
				}
			}
		});
	}
});

frappe.ui.form.on("Stock Entry", "purpose", function(frm) {
	set_destination_warehouse(frm);
	if (cur_frm.doc.purpose == "Material Transfer") {
		cur_frm.set_value("spn_stock_entry_type", "Skynpro");
	} else {
		cur_frm.set_value("spn_stock_entry_type", "Default");
	}
});

frappe.ui.form.on("Stock Entry", "spn_stock_entry_type", function(frm) {
	set_destination_warehouse(frm);
});

//Helpers
function set_destination_warehouse(frm){
	if (frm.doc.purpose == "Material Transfer") {
		if (frm.doc.spn_stock_entry_type == "Skynpro") {
			frm.set_df_property("spn_to_warehouse", "hidden", 0);
			frappe.model.get_value("SPN Settings", "SPN Settings", "spn_transit_warehouse", function(r){					     
				cur_frm.set_value("to_warehouse", r.spn_transit_warehouse);
			});
		} else {
			frm.set_df_property("spn_to_warehouse", "hidden", 1);
			cur_frm.set_value("to_warehouse", "");
		}
	} else {
		frm.set_df_property("spn_to_warehouse", "hidden", 1);
		cur_frm.set_value("to_warehouse", "");
	}
}

function fetch_and_set_items(frm, transit_entry_name) {
	items = [];
	frappe.call({
		method: "skynpronaturals_erpnext.api.get_items_from_transit_entry",
		args: { 
			"transit_entry_name" : transit_entry_name 
		},
		callback: function(r) {
			console.log(r);
		}
	});
}
